
public class two { // 2번 문제

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int mynum = 5; // 4바이트 정수형 mynum변수에 5값 대입
		float myfloatnum =  5.99f; // 4바이트 실수형 myfloatnum변수에 5.99f값 대입
		char myletter = 'D'; // 문자형 2바이트 myletter변수에 D값 대입
		boolean mybool = true; // 실수형 8바이트 mybool에 참값 대입
		String mytext = "Hello"; // 기본타입 문자열 mytext변수에 Hello값 대입
		
		System.out.println(mynum);
		System.out.println(myfloatnum);
		System.out.println(myletter);
		System.out.println(mybool);
		System.out.println(mytext);
		
		// 각 라인마다 1줄씩 띄워서 값 출력
		
	}

}